import {useEffect, useState} from 'react'
import axios from "axios";
import Card from './Card';
import './photo.css'
function Photo() {
    const [loding,setloding]=useState(true);
   
    const [offset,setoffset]=useState(0);
    const [limits,setlimits]=useState(20);
    const [url,seturl]= useState(`https://api.slingacademy.com/v1/sample-data/photos?offset=${offset}&limit=${limits}`);
    

    const [myphoto,setmyPhoto]=useState([]);
    async function fetchData() {
       await axios.get(url).then((res)=>{
        setmyPhoto(res.data.photos)
    })
    }
    function previmg() {
        const Newoffset=offset-20;
        setlimits((n)=>(n-20));
        setoffset(Newoffset);
        seturl(`https://api.slingacademy.com/v1/sample-data/photos?offset=${offset}&limit=${limits}`)
        
    }
    function nextimg() {
        setlimits((n)=>(n+20));
        setoffset((n)=>(n+20));
        seturl(`https://api.slingacademy.com/v1/sample-data/photos?offset=${offset}&limit=${limits}`)
    }
    useEffect(()=>{
        
        fetchData();
        setloding(false);
       
    },[url])
    return(
       <div className='allDisplay'>
         <div className='photos'>
         {myphoto?.map((n)=>(
            <Card img={n.url} id={n.id} description={n.description}/>
         ))}
         </div>
         <div className='btn'>
         <button onClick={previmg} className='prevBtn'>Prev</button>
         <button onClick={nextimg} className='nextBtn'>Next</button>
         </div>
       </div>
        
    );
}

export default Photo;
