import {useEffect, useState} from 'react'
import axios from "axios";
import Card from './Card';
import './photo.css';
function Photo() {
    const url=`https://api.slingacademy.com/v1/sample-data/photos?offset=${images.offset}&limit=${images.limits}`;
    const [images,setimages]=useState({
        loding:true,
        offset:0,
        limits:20,
        url:url,
        myphoto:[],
    });
    

    async function fetchData() {
       await axios.get(images.url).then((res)=>{
        setimages({
            ...images,
            myphoto:res.data.photos,

        });
    })
    }
    function previmg() {
        const Newoffset=images.offset-20;
        const Newlimits=images.limits-20;
        setimages({
            ...images,
            offset:Newoffset,
            limits:Newlimits,
            loding:true,
            url:`https://api.slingacademy.com/v1/sample-data/photos?offset=${images.offset}&limit=${images.limits}`,
        });
    
   
        
    }
    function nextimg() {
        const Newoffset=images.offset+20;
        const Newlimits=images.limits+20;
        setimages({
            ...images,
            offset:Newoffset,
            limits:Newlimits,
            loding:true,
            url:`https://api.slingacademy.com/v1/sample-data/photos?offset=${images.offset}&limit=${images.limits}`,
        });
    

    }
    useEffect(()=>{
        
        fetchData();
       setimages({
        ...images,
        loding:false,
       })
       
    },[images.url])
    return(
       <div className='allDisplay'>
         <div className='photos'>
         {images.myphoto?.map((n)=>(
            <Card key={n.id} img={n.url} id={n.id} description={n.description}/>
         ))}
         </div>
         <div className='btn'>
         <button onClick={previmg} className='prevBtn'>Prev</button>
         <button onClick={nextimg} className='nextBtn'>Next</button>
         {images.loding?<p className='loading'>Loading</p>:null}
         </div>
       </div>
        
    );
}

export default Photo;
